#Combien y a-t-il de handle dans cette array ?
puts "1 - Combien y a-t-il de handle dans cette liste ?"
puts "   => Il y a #{journalistes.count} journalistes dans cette liste\r\n"
puts '---'

#Quel est le handle le plus court de cette liste ?
puts "2 - Quel est le handle le plus court dans cette liste ?"
nbrLettres = []

journalistes.each do |nbr|
  lettres = nbr.length
  nbrLettres.push(lettres)
end

position = nbrLettres.index(nbrLettres.min)

puts "Le handle le plus court est #{journalistes[position]}"
puts '---'

#Combien y-a-t'il de handle contenant 5 caractères (le @ ne compte pas pour un caractère)
puts "3 - Combien y-a-t'il de handle contenant 5 caractères ?"
nbrLettresBis = []

journalistes.each do |nbr|
  lettres = nbr.length
  nbrLettresBis.push(lettres)
end

result = nbrLettresBis.count { |x| x == 6 }
puts "Il y a #{result} handles avec moins de 5 caractères"
puts '---'

#Combien commencent par une majuscule (première lettre juste après le @) ?
puts "4 - Combien y a t'il de Handle avec des majuscules ?"

sliced = []

journalistes.each do |n|
  coupe = n[0..1]
  sliced.push(coupe)
end

sommeCapitales = []

sliced.each do |cap|
  capitales = cap.scan(/[A-Z]/).length
  sommeCapitales.push(capitales)
end

puts sommeCapitales.inject(0, :+)
puts "---"

#Trie la liste de handle par ordre alphabétique.
puts "5 - La liste classée dans l'ordre alphabétique :"
alphabetique = journalistes.sort { |a, b| a <=> b}
puts alphabetique
puts '---'

#Trie la liste de handle par taille des handle (les plus petits en premiers, les plus grands après)
puts "6 - Le liste des handle classé par la taille des handles :"
nbrKey = []

journalistes.each do |nbr|
  lettres = nbr.length
  nbrKey.push(lettres)
end

h = Hash[nbrKey.zip(journalistes)] 
trie = h.sort.to_h

trie.each do |key, value|
  puts value
end

puts '---'

#Quelle est la position dans l'array de la personne @epenser ?
puts "7 - Quelle est la postion de @epenser dans la liste ?"
puts "   => @epenser est #{journalistes.index('@epenser')+1}ème dans la liste\r"
puts '---'

#Sors-moi une répartition des handle par taille de ces derniers (nombre de handle avec 1 caractère, nombre de handle avec 2 caractères, nombre de handle avec 3 caractères, etc)
puts "8 - Le liste des handle classé nombre de caractères :"
nbrKeyBis = []

journalistes.each do |nbr|
  lettres = nbr.length
  nbrKeyBis.push(lettres)
end

h = Hash[nbrKeyBis.zip(journalistes)] 

h.sort.map do |key,value|
  puts "#{key}:#{value}"
end

puts '---'
